package finalQuestion;

import java.awt.Color;
import java.awt.Graphics;
import java.awt.Graphics2D;


public class BouncerBox {
	
	private int x;
	private int y;
	private int width;
	private int height;
	
	public BouncerBox(int x, int y, int width, int height){
		this.x = x;
		this.y = y;
		this.width=width;
		this.height = height;
	}
	
	
	public void draw(Graphics2D g2) {
		// TODO Auto-generated method stub
		g2.setColor(Color.BLUE);
		g2.drawRect(this.x, this.y, this.width, this.height);
	}
}
