import java.awt.Dimension;

import javax.swing.JFrame;

/**
 * Draws a scene, via the SceneComponent and using other classes such as House and PineTree.
 * 
 * Constructs and display a JFrame
 * 
 */

public class SceneViewer {
	public static final Dimension SCENE_VIEWER_SIZE = new Dimension(750, 600);

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		JFrame frame = new JFrame();
		frame.setSize(SCENE_VIEWER_SIZE);
		frame.setTitle("I see a beautiful scene! ");

		frame.add(new SceneComponent());

		frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
		frame.setVisible(true);
	}

}